package com.testwa.distest.client.android;import com.android.ddmlib.AndroidDebugBridge;import com.android.ddmlib.IDevice;import com.github.cosysoft.device.shell.AndroidSdk;public class AndroidDebugBridgeWrapper {    /**     * android bridge     */    private AndroidDebugBridge mAdbBridge;    public AndroidDebugBridgeWrapper() {    }    public void init(boolean clientSupport) {        AndroidDebugBridge.initIfNeeded(clientSupport);        mAdbBridge = AndroidDebugBridge.getBridge();        if (mAdbBridge == null) {            mAdbBridge = AndroidDebugBridge.createBridge(AndroidSdk.adb().getAbsolutePath(), false);        }    }    /**     * 注册设备监听器     *     * @param listener 监听器     */    public void addDeviceChangeListener(AndroidDebugBridge.IDeviceChangeListener listener) {        AndroidDebugBridge.addDeviceChangeListener(listener);    }    /**     * 移除监听器     *     * @param listener 监听器     */    public void removeDeviceChangeListener(AndroidDebugBridge.IDeviceChangeListener listener) {        AndroidDebugBridge.removeDeviceChangeListener(listener);    }    public void terminate() {        AndroidDebugBridge.terminate();    }    public void disconnectBridge() {        AndroidDebugBridge.disconnectBridge();    }}  