package com.testwa.distest.client.callback;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.JSONObject;import com.google.protobuf.ByteString;import com.testwa.core.common.enums.Command;import com.testwa.distest.client.DeviceClient;import lombok.extern.slf4j.Slf4j;import org.apache.commons.lang3.StringUtils;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-08 16:36 **/@Slf4jpublic class ComponentStartCallback implements ICallBack<String> {    private DeviceClient client;    public ComponentStartCallback(DeviceClient client){        this.client = client;    }    @Override    public void callback(ByteString bytes) {        String cmd = bytes.toStringUtf8();        client.startMinitouch();        client.startMinicap(cmd);    }}