package com.testwa.distest.client.callback;import com.github.cosysoft.device.shell.AndroidSdk;import com.google.protobuf.ByteString;import com.testwa.distest.client.android.AndroidHelper;import com.testwa.distest.client.DeviceClient;import com.testwa.distest.client.component.ADBCommandUtils;import lombok.extern.slf4j.Slf4j;import org.openqa.selenium.os.CommandLine;import java.io.IOException;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-08 20:23 **/@Slf4jpublic class InputCallback implements ICallBack<String> {    private DeviceClient client;    public InputCallback(DeviceClient client){        this.client = client;    }    @Override    public void callback(ByteString bytes) {        String cmd = bytes.toStringUtf8();        log.debug(client+ ":callback-log" + cmd);        ADBCommandUtils.switchADBKeyBoard(client.getClientId());        ADBCommandUtils.inputText(client.getClientId(), cmd, 5000L);    }}