package com.testwa.distest.client.callback;import com.google.protobuf.ByteString;import com.testwa.distest.client.DeviceClient;import com.testwa.distest.client.component.ADBCommandUtils;import lombok.extern.slf4j.Slf4j;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-08 16:36 **/@Slf4jpublic class UninstallAppCallback implements ICallBack<String> {    private DeviceClient client;    public UninstallAppCallback(DeviceClient client){        this.client = client;    }    @Override    public void callback(ByteString bytes) {        String command = bytes.toStringUtf8();        log.debug(client+ ":callback-log " + command);        ADBCommandUtils.uninstallApp(client.getClientId(), command);    }}