package com.testwa.distest.client.component.executor;import com.testwa.distest.client.component.executor.worker.AbstractExecutor;public abstract class AbstractExecutorHandler {    private AbstractExecutorHandler handler;    public abstract void handleRequest(AbstractExecutor executor) throws Exception;    public AbstractExecutorHandler getHandler() {        return handler;    }    public void setHandler(AbstractExecutorHandler handler) {        this.handler = handler;    }}