package com.testwa.distest.client.component.executor;import com.testwa.core.cmd.RemoteRunCommand;import com.testwa.distest.client.ApplicationContextUtil;import com.testwa.distest.client.component.appium.AppiumManager;import com.testwa.distest.client.component.appium.pool.AppiumManagerPool;import com.testwa.distest.client.component.executor.worker.AndroidExecutorFactory;import com.testwa.distest.client.component.executor.worker.CrawlerAbstractExecutor;import com.testwa.distest.client.component.executor.worker.HGAbstractExecutor;import com.testwa.distest.client.service.GrpcClientService;import lombok.extern.slf4j.Slf4j;@Slf4jpublic class CrawlerTestTask extends AbstractTestTask{    private RemoteRunCommand cmd;    private CrawlerAbstractExecutor executor;    private AppiumManagerPool pool;    private AppiumManager manager;    private TestTaskListener listener;    public CrawlerTestTask(RemoteRunCommand cmd, TestTaskListener listener){        this.cmd = cmd;        this.listener = listener;    }    public void start() {        pool = (AppiumManagerPool) ApplicationContextUtil.getBean("appiumManagerPool");        GrpcClientService grpcClientService = (GrpcClientService) ApplicationContextUtil.getBean("grpcClientService");        manager = pool.getManager();        String appiumLogPath = manager.getAppiumlogPath();        if(manager.getAppiumService().isRunning()){            try {                String appiumUrl = manager.getAppiumService().getUrl().toString();                AndroidExecutorFactory executorFactory = new AndroidExecutorFactory();                executor = executorFactory.getCrawlerTask(cmd);                executor.init(appiumUrl, cmd);                AppDownloadExecutorHandler appHander = new AppDownloadExecutorHandler();                ExecutorHandler pythonHander = new ExecutorHandler();                appHander.setHandler(pythonHander);                appHander.handleRequest(executor);                grpcClientService.missionComplete(cmd.getTaskCode(), cmd.getDeviceId());            } catch (Exception e){                log.error("{} 任务执行错误", cmd.getDeviceId(), e);                grpcClientService.gameover(cmd.getTaskCode(), cmd.getDeviceId(), e.getMessage());            } finally {                grpcClientService.appiumLogUpload(cmd.getTaskCode(), cmd.getDeviceId(), appiumLogPath);            }        }else{            log.error("Appium 启动失败");            grpcClientService.gameover(cmd.getTaskCode(), cmd.getDeviceId(), "Appium 启动失败");        }        pool.release(manager);        listener.taskFinish();    }    public void kill() {        log.info("设备 {} 测试任务被停止", cmd.getDeviceId());        if (executor != null) {            executor.stop();        }        if(pool != null){            pool.release(manager);        }    }}