package com.testwa.distest.client.component.executor;import com.testwa.distest.client.component.executor.factory.AbstractExecutor;import com.testwa.distest.client.component.executor.factory.HGAbstractExecutor;import com.testwa.distest.client.exception.DownloadFailException;import java.io.IOException;public class ScriptDownloadExecutorHandler extends AbstractExecutorHandler {    @Override    public void handleRequest(AbstractExecutor executor) throws DownloadFailException, IOException {        HGAbstractExecutor e = (HGAbstractExecutor) executor;        e.downloadScript();        super.getHandler().handleRequest(executor);    }}