package com.testwa.distest.client.component.executor;import com.testwa.distest.client.component.executor.worker.AbstractExecutor;import com.testwa.distest.client.component.executor.worker.HGAbstractExecutor;public class ScriptDownloadExecutorHandler extends AbstractExecutorHandler {    @Override    public void handleRequest(AbstractExecutor executor) throws Exception {        HGAbstractExecutor e = (HGAbstractExecutor) executor;        e.downloadScript();        super.getHandler().handleRequest(executor);    }}