package com.testwa.distest.client.component.minicap;import lombok.extern.slf4j.Slf4j;import org.apache.commons.io.IOUtils;import java.io.Closeable;@Slf4jpublic class ScreenIOSProjection {    private int port;    private IOSScreenServer server;    private ScreenProjectionObserver screenProjectionObserver;    public ScreenIOSProjection(Integer port, ScreenProjectionObserver screenProjectionObserver) {        this.port = port;        this.screenProjectionObserver = screenProjectionObserver;    }    public void startServer() {        this.server = new IOSScreenServer(port);        this.server.registerObserver(screenProjectionObserver);        this.server.start();    }    public boolean isRunning() {        if(this.server == null) {            return false;        }        return this.server.isRunning();    }    public void close() {        IOUtils.closeQuietly(this.server);    }}