package com.testwa.distest.client.component.stfagent;import lombok.Data;import java.net.Socket;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-07-13 20:25 **/@Datapublic class DevInformationAssembly {    private String devSerial;    private DevProperty devProperty;    private DevDoIdentify devDoIdentify;    private DevVision devVision;    private DevDisplay devDisplay;    private DevSdcardStatus devSdcardStatus;    private DevWifiStatus devWifiStatus;    public DevInformationAssembly(String devSerial,Socket socket){        this.devSerial = devSerial;        devProperty = new DevProperty(devSerial,socket);        devDoIdentify = new DevDoIdentify(devSerial,socket);        devVision = new DevVision(devSerial,socket);        devDisplay = new DevDisplay(devSerial,socket);        devSdcardStatus = new DevSdcardStatus(devSerial,socket);        devWifiStatus = new DevWifiStatus(devSerial,socket);    }}