package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.io.IOException;import java.net.Socket;import java.util.ArrayList;import java.util.HashMap;import java.util.Iterator;@Datapublic class DevProperty {    private ArrayList<String> properties = new ArrayList<>();    private String devSerial;    private Socket socket;    private HashMap<String,String> devPropertiesHashMap = new HashMap<>();    public DevProperty(String devSerial, Socket socket){        this.devSerial = devSerial;        this.socket = socket;        properties.add("imei");        properties.add("imsi");        properties.add("phoneNumber");        properties.add("iccid");        properties.add("operator");        properties.add("network");    }    public ArrayList<String> getDevProperties(){        return this.properties;    }    public Wire.Envelope getPropertiesEnvelope(){        Wire.GetPropertiesRequest.Builder getPropertiesBuilder= Wire.GetPropertiesRequest.newBuilder();        Iterator<String> iterator = getDevProperties().iterator();        if(iterator.hasNext()){            getPropertiesBuilder.addProperties(iterator.next());        }        Wire.GetPropertiesRequest request = getPropertiesBuilder.build();        Wire.Envelope.Builder envelopBuilder1 = Wire.Envelope.newBuilder();        envelopBuilder1.setType(Wire.MessageType.GET_PROPERTIES);        envelopBuilder1.setMessage(request.toByteString());        Wire.Envelope envelope1 = envelopBuilder1.build();        return envelope1;    }}