package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.net.Socket;@Datapublic class DevSdcardStatus {    private String devSerial = null;    private boolean mounted = false;    public DevSdcardStatus(String devSerial){        this.devSerial = devSerial;    }    public Wire.Envelope getSdcardStatusEnvelope(){        Wire.GetSdStatusRequest.Builder getSdcardStatus = Wire.GetSdStatusRequest.newBuilder();        Wire.Envelope.Builder envelopBuilder = Wire.Envelope.newBuilder();        envelopBuilder.setType(Wire.MessageType.GET_SD_STATUS);        envelopBuilder.setMessage(getSdcardStatus.build().toByteString());        return envelopBuilder.build();    }}