package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.net.Socket;/** * Created by gtguo on 2/18/2017. */@Datapublic class DevVision {    private String devSerial;    private String devVision;    public DevVision(String devSerial){        this.devSerial = devSerial;    }    public Wire.Envelope getDevVisionEnvelope(){        Wire.GetVersionRequest.Builder getVersion = Wire.GetVersionRequest.newBuilder();        Wire.Envelope.Builder envelopBuilder = Wire.Envelope.newBuilder();        envelopBuilder.setType(Wire.MessageType.GET_VERSION);        envelopBuilder.setMessage(getVersion.build().toByteString());        return envelopBuilder.build();    }}