package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.net.Socket;/** * Created by gtguo on 2/18/2017. */@Datapublic class DevVision {    private String devSerial;    private Socket socket;    private String devVision;    public DevVision(String devSerial,Socket socket){        this.devSerial = devSerial;        this.socket = socket;    }    public Wire.Envelope getDevVisionEnvelope(){        Wire.GetVersionRequest.Builder getVersion = Wire.GetVersionRequest.newBuilder();        Wire.GetVersionRequest getVersionRequest = getVersion.build();        Wire.Envelope.Builder envelopBuilder = Wire.Envelope.newBuilder();        envelopBuilder.setType(Wire.MessageType.GET_VERSION);        envelopBuilder.setMessage(getVersionRequest.toByteString());        Wire.Envelope envelope = envelopBuilder.build();        return envelope;    }}