package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.net.Socket;/** * Created by gtguo on 2/18/2017. */@Datapublic class DevWifiStatus {    private String devSerial;    private boolean wifiStatus = false;    public DevWifiStatus(String devSerial){        this.devSerial = devSerial;    }    public Wire.Envelope getWifiStatusEnvelope(){        Wire.GetWifiStatusRequest.Builder getWifiStatus = Wire.GetWifiStatusRequest.newBuilder();        Wire.Envelope.Builder envelopBuilder = Wire.Envelope.newBuilder();        envelopBuilder.setType(Wire.MessageType.GET_WIFI_STATUS);        envelopBuilder.setMessage(getWifiStatus.build().toByteString());        return envelopBuilder.build();    }}