package com.testwa.distest.client.component.stfagent;import jp.co.cyberagent.stf.proto.Wire;import lombok.Data;import java.net.Socket;/** * Created by gtguo on 2/18/2017. */@Datapublic class DevWifiStatus {    private String devSerial;    private Socket socket;    private boolean wifiStatus = false;    public DevWifiStatus(String devSerial,Socket socket){        this.devSerial = devSerial;        this.socket = socket;    }    public Wire.Envelope getWifiStatusEnvelope(){        Wire.GetWifiStatusRequest.Builder getWifiStatus = Wire.GetWifiStatusRequest.newBuilder();        Wire.GetWifiStatusRequest getWifiStatusRequest = getWifiStatus.build();        Wire.Envelope.Builder envelopBuilder = Wire.Envelope.newBuilder();        envelopBuilder.setType(Wire.MessageType.GET_WIFI_STATUS);        envelopBuilder.setMessage(getWifiStatusRequest.toByteString());        Wire.Envelope envelope = envelopBuilder.build();        return envelope;    }}