package com.testwa.distest.client.control.client.callback;import com.github.cosysoft.device.android.AndroidDevice;import com.github.cosysoft.device.android.impl.AndroidDeviceStore;import com.google.protobuf.ByteString;import com.testwa.distest.client.control.client.DeviceClient;import lombok.extern.slf4j.Slf4j;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-08 16:36 **/@Slf4jpublic class ShellCallback implements ICallBack<String> {    private DeviceClient client;    public ShellCallback(DeviceClient client){        this.client = client;    }    @Override    public void callback(ByteString bytes) {        String command = bytes.toStringUtf8();        log.debug(client+ ":callback-log" + command);        client.shellCommand(command);    }}