package com.testwa.distest.client.control.event;import com.testwa.distest.client.grpc.GrpcClient;import com.testwa.distest.client.grpc.Gvice;import io.grpc.Channel;import io.rpc.testwa.device.NoUsedDeviceRequest;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Componentpublic class DeviceDisconnectListener implements ApplicationListener<DeviceDisconnectEvent> {    @GrpcClient("local-grpc-server")    private Channel serverChannel;    @Override    @Async    public void onApplicationEvent(DeviceDisconnectEvent event) {        NoUsedDeviceRequest disconnectDevice = NoUsedDeviceRequest.newBuilder()                .setDeviceId(event.getDeviceId())                .build();        Gvice.deviceService(serverChannel).disconnect(disconnectDevice);    }}