package com.testwa.distest.client.control.event;import com.testwa.distest.client.grpc.GrpcClient;import com.testwa.distest.client.grpc.Gvice;import io.grpc.Channel;import io.rpc.testwa.device.DisconnectedRequest;import lombok.extern.log4j.Log4j2;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Log4j2@Componentpublic class DeviceOfflineListener implements ApplicationListener<DeviceDisconnectEvent> {    @GrpcClient("local-grpc-server")    private Channel serverChannel;    @Async    @Override    public void onApplicationEvent(DeviceDisconnectEvent event) {        log.info("Event: device " + event.getDeviceId() + " offline");        DisconnectedRequest request = DisconnectedRequest.newBuilder()                .setDeviceId(event.getDeviceId())                .build();        Gvice.deviceService(serverChannel).disconnect(request);    }}