package com.testwa.distest.client.device.listener.callback.remote;import com.alibaba.fastjson.JSON;import com.github.cosysoft.device.android.AndroidDevice;import com.github.cosysoft.device.android.impl.AndroidDeviceStore;import com.google.protobuf.ByteString;import com.testwa.core.cmd.AppInfo;import com.testwa.distest.client.android.ADBCommandUtils;import com.testwa.distest.client.component.Constant;import com.testwa.distest.client.component.appium.utils.Config;import com.testwa.distest.client.device.driver.IDeviceRemoteControlDriver;import com.testwa.distest.client.device.listener.callback.IRemoteCommandCallBack;import com.testwa.distest.client.download.Downloader;import com.testwa.distest.client.exception.DownloadFailException;import com.testwa.distest.client.ios.IOSDeviceUtil;import io.rpc.testwa.device.DeviceType;import lombok.extern.slf4j.Slf4j;import java.io.File;import java.io.IOException;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-08 16:36 **/@Slf4jpublic class InstallAppCallback implements IRemoteCommandCallBack<ByteString> {    private IDeviceRemoteControlDriver client;    public InstallAppCallback(IDeviceRemoteControlDriver client){        this.client = client;    }    @Override    public void callback(ByteString bytes) {        String command = bytes.toStringUtf8();        client.installApp(command);    }}