package com.testwa.distest.client.event;import com.github.cosysoft.device.android.AndroidDevice;import com.github.cosysoft.device.android.impl.AndroidDeviceStore;import com.github.cosysoft.device.exception.DeviceNotFoundException;import com.testwa.distest.client.service.DeviceService;import com.testwa.distest.client.service.GrpcClientService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class DeviceConnectedListener implements ApplicationListener<DeviceConnectedEvent> {    @Autowired    private GrpcClientService grpcClientService;    @Autowired    private DeviceService deviceService;    @Async    @Override    public void onApplicationEvent(DeviceConnectedEvent event) {        log.info("Event: 设备 " + event.getDeviceId() + " 已连接");        try {            AndroidDevice device = AndroidDeviceStore.getInstance().getDeviceBySerial(event.getDeviceId());            if(device != null){                grpcClientService.initDevice(device);            }        }catch (DeviceNotFoundException e){            log.error("无法找到设备 " + event.getDeviceId());        }    }}