package com.testwa.distest.client.event;import com.testwa.distest.client.control.client.Clients;import com.testwa.distest.client.grpc.GrpcClient;import io.grpc.Channel;import lombok.extern.slf4j.Slf4j;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class DeviceDisconnectListener implements ApplicationListener<DeviceDisconnectEvent> {    @GrpcClient("local-grpc-server")    private Channel serverChannel;    @Async    @Override    public void onApplicationEvent(DeviceDisconnectEvent event) {        log.info("Event: 设备 " + event.getDeviceId() + " 断开，即将停止所有任务");        Clients.remove(event.getDeviceId());    }}