package com.testwa.distest.client.event;import com.testwa.distest.client.control.client.Clients;import com.testwa.distest.client.service.GrpcClientService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class DeviceDisconnectListener implements ApplicationListener<DeviceDisconnectEvent> {    @Autowired    private GrpcClientService grpcClientService;    @Async    @Override    public void onApplicationEvent(DeviceDisconnectEvent event) {        log.info("Event: 设备 " + event.getDeviceId() + " 断开，即将停止所有任务");        grpcClientService.deviceDisconnect(event.getDeviceId());        Clients.remove(event.getDeviceId());    }}