package com.testwa.distest.client.event;import com.testwa.distest.client.control.client.Clients;import com.testwa.distest.client.grpc.GrpcClient;import io.grpc.Channel;import lombok.extern.log4j.Log4j2;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Log4j2@Componentpublic class DeviceDisconnectListener implements ApplicationListener<DeviceDisconnectEvent> {    @GrpcClient("local-grpc-server")    private Channel serverChannel;    @Async    @Override    public void onApplicationEvent(DeviceDisconnectEvent event) {        log.info("Event: device " + event.getDeviceId() + " disconnected");        Clients.remove(event.getDeviceId());    }}