package com.testwa.distest.client.event;import com.testwa.distest.client.service.GrpcClientService;import lombok.extern.log4j.Log4j2;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Log4j2@Componentpublic class ExecutorLogNotifyListener implements ApplicationListener<ExecutorLogNotifyEvent> {    @Autowired    private GrpcClientService grpcClientService;    @Async    @Override    public void onApplicationEvent(ExecutorLogNotifyEvent event) {        grpcClientService.executorLogUpload(event.getLogInfo());    }}