package com.testwa.distest.client.event;import lombok.Data;import org.springframework.context.ApplicationEvent;@Datapublic class UploadFileToServerEvent extends ApplicationEvent {    private Long taskId;    private String deviceId;    private FileType type;    private String filePath;    public UploadFileToServerEvent(Object source, Long taskId, String deviceId, FileType type, String filePath){        super(source);        this.taskId = taskId;        this.deviceId = deviceId;        this.type = type;        this.filePath = filePath;    }    public enum FileType{        LOGCAT, SCREENSHOOT, APPIUMLOG    }}