package com.testwa.distest.client.component.stfagent;import com.testwa.distest.client.DistestClientApplication;import com.testwa.distest.client.service.GrpcClientService;import lombok.extern.slf4j.Slf4j;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.TestPropertySource;import org.springframework.test.context.junit4.SpringRunner;import java.util.concurrent.TimeUnit;import static org.junit.Assert.*;@Slf4j@RunWith(SpringRunner.class)@SpringBootTest(classes = DistestClientApplication.class)@TestPropertySource(locations="classpath:application-test.properties")public class StfAgentServerTest {    @Autowired    private GrpcClientService service;    @Test    public void testAgentServerStart() {        StfAgentServer stfAgentServer = new StfAgentServer("8c2b6aee", "");        stfAgentServer.start();        int time = 100;        while (time > 0) {            try {                TimeUnit.SECONDS.sleep(1);            } catch (InterruptedException e) {                e.printStackTrace();            }            time--;        }    }}