package com.testwa.core.shell;/** * @Program: distest * @Description: 代码输出回调 * @Author: wen * @Create: 2018-04-17 11:22 **/public class CodeInfoCallback implements OutputCallback {    private StringBuffer codeInfo = new StringBuffer();    @Override    public void parse(String line) {        codeInfo.append(line).append("\n");    }    public String getCodeInfo() {        return codeInfo.toString();    }}