package com.testwa.distest.message;import org.springframework.amqp.rabbit.annotation.EnableRabbit;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.data.mongo.MongoDataAutoConfiguration;import org.springframework.boot.autoconfigure.mongo.MongoAutoConfiguration;import org.springframework.context.annotation.EnableAspectJAutoProxy;import org.springframework.scheduling.annotation.AsyncConfigurerSupport;import org.springframework.scheduling.annotation.EnableAsync;import org.springframework.scheduling.annotation.EnableScheduling;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-31 10:31 **/@SpringBootApplication(exclude = {MongoAutoConfiguration.class, MongoDataAutoConfiguration.class})@EnableScheduling@EnableAsync@EnableAspectJAutoProxy(proxyTargetClass = true)@EnableRabbitpublic class DistestMessageApplication extends AsyncConfigurerSupport {    public static void main(String[] args) {        SpringApplication.run(DistestMessageApplication.class, args);    }}