package com.testwa.distest.message.setup;import io.github.biezhi.ome.OhMyEmail;import lombok.extern.slf4j.Slf4j;import org.springframework.boot.CommandLineRunner;import org.springframework.core.annotation.Order;import org.springframework.stereotype.Component;import java.util.Properties;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-31 19:55 **/@Slf4j@Order(value=1)@Componentpublic class EmailSetUp implements CommandLineRunner {    @Override    public void run(String... strings) throws Exception {        Properties props = OhMyEmail.defaultConfig(true);        props.put("mail.smtp.host", "smtp.mxhichina.com");        props.put("mail.smtp.port", "465");        props.put("mail.smtp.ssl.enable", "true");        OhMyEmail.config(props, "service@testwa.com", "Appium12");    }}