package com.testwa.distest.common.android;import java.util.Arrays;import java.util.List;/** * @Program: distest * @Description: android各个系统和sdk的对应关系 * @Author: wen * @Create: 2018-04-18 11:06 **/public class AndroidOSInfo {    private static final List<String> sdkLevels = Arrays.asList("10", "15", "16", "17", "18", "19", "21", "22", "23", "24", "25", "26", "27");    /**     *@Description: 平台版本的对应关系：https://developer.android.com/about/dashboards/index.html     *@Param: [sdk]     *@Return: java.lang.String     *@Author: wen     *@Date: 2018/4/18     */    public static String getOSVersionFromSDKLevel(String sdk){        String osVersion = "";        if(sdkLevels.contains(sdk)){            switch (sdk){                case "10":                    osVersion = "2.3.3 - 2.3.7";                    break;                case "15":                    osVersion = "4.0.3 - 2.3.4";                    break;                case "16":                    osVersion = "4.1.x";                    break;                case "17":                    osVersion = "4.2.x";                    break;                case "18":                    osVersion = "4.3";                    break;                case "19":                    osVersion = "4.4";                    break;                case "21":                    osVersion = "5.0";                    break;                case "22":                    osVersion = "5.1";                    break;                case "23":                    osVersion = "6.0";                    break;                case "24":                    osVersion = "7.0";                    break;                case "25":                    osVersion = "7.1";                    break;                case "26":                    osVersion = "8.0";                    break;                case "27":                    osVersion = "8.1";                    break;            }        }        return osVersion;    }}