package com.testwa.distest.common.shell;/** * @Program: distest * @Description: 错误输出回调 * @Author: wen * @Create: 2018-04-17 11:24 **/public class ErrorOutputCallback implements OutputCallback{    private StringBuffer errors = new StringBuffer();    @Override    public void parse(String line) {        errors.append(line).append("\n");    }    public String getErrors() {        return errors.toString();    }}