package com.testwa.distest.common.util;import java.io.*;import java.util.zip.ZipEntry;import java.util.zip.ZipFile;/** * @Program: distest * @Description: 从APK里解压除icon图片并存放在磁盘上 * @Author: wen * @Create: 2018-04-11 15:24 **/public class IconUtil {    public static InputStream extractFileFromApk(String apkpath, String fileName) {        try {            ZipFile zFile = new ZipFile(apkpath);            ZipEntry entry = zFile.getEntry(fileName);            entry.getComment();            entry.getCompressedSize();            entry.getCrc();            entry.isDirectory();            entry.getSize();            entry.getMethod();            InputStream stream = zFile.getInputStream(entry);            return stream;        } catch (IOException e) {            e.printStackTrace();        }        return null;    }    public static void extractFileFromApk(String apkpath, String applicationIcon, String outputPath) throws Exception {        InputStream is = extractFileFromApk(apkpath, applicationIcon);        File file = new File(outputPath);        BufferedOutputStream bos = new BufferedOutputStream(new FileOutputStream(file), 1024);        byte[] b = new byte[1024];        BufferedInputStream bis = new BufferedInputStream(is, 1024);        while(bis.read(b) != -1){            bos.write(b);        }        bos.flush();        is.close();        bis.close();        bos.close();    }}