package com.testwa.distest.server.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import com.testwa.core.base.mybatis.annotation.Column;import com.testwa.core.base.mybatis.annotation.Table;import com.testwa.distest.common.enums.DB;import lombok.Data;import java.util.Date;/** * @Program: distest * @Description: 任务详情，对应的是每个设备 * @Author: wen * @Create: 2018-05-02 17:41 **/@Data@Table(name="sub_task")public class SubTask extends ProjectBaseEntity {    @Column(name = "taskCode")    private Long taskCode;    @Column(name = "deviceId")    private String deviceId;    @Column(name = "taskType")    private DB.TaskType taskType;    @Column(name = "status")    private DB.TaskStatus status;    @Column(name = "errorMsg")    private String errorMsg;    @Column(name = "endTime")    private Date endTime;    @Column(name = "video")    private String video;    @JsonIgnore    @Column(name = "lock_version")    private Long lockVersion;}