package com.testwa.distest.server.mongo.model;import lombok.Data;import lombok.ToString;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-25 18:14 **/@Data@Document(collection = "t_crash_log")@ToStringpublic class CrashLog {    @Id    private String id;    @Indexed    private String deviceId;    @Indexed    private Long taskId;    private String count;    private String procName;    private String exception;    private String content;}