package com.testwa.distest.server.mongo.model;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;@Data@Document(collection = "t_executor_log_info")public class ExecutorLogInfo {    @Id    private String id;    private String token;    private Long timestamp;    @Indexed    private Long taskId;    @Indexed    private String deviceId;    private String action;    private int actionOrder;    private String args;    private String flag;    @Indexed    private String methodName;}