package com.testwa.distest.server.mongo.model;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;@Data@Document(collection = "t_method_running_log")public class MethodRunningLog {    @Id    private String id;    private String token;    private Long timestamp;    @Indexed    private Long taskId;    @Indexed    private String deviceId;    private String methodDesc;    private int methodOrder;    private String args;    private String flag;    @Indexed    private String methodName;    public static String getCollectionName() {        Document doc = MethodRunningLog.class.getAnnotation(Document.class);        return doc.collection();    }}