package com.testwa.distest.server.mongo.model;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;/** * @Program: distest * @Description: monkey执行的点击步骤，主要是保存截图和任务的关系 * @Author: wen * @Create: 2018-05-21 21:49 **/@Data@Document(collection = "t_step")public class Step {    @Id    private String id;    @Indexed    private String deviceId;    @Indexed    private Long taskId;    private String img;    private String dump;}