package com.testwa.distest.server.mongo.service;import com.testwa.distest.server.mongo.model.ProcedureInfo;import com.testwa.distest.server.mongo.model.Step;import com.testwa.distest.server.mongo.repository.ProcedureInfoRepository;import com.testwa.distest.server.mongo.repository.StepRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.stereotype.Service;import java.util.List;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-21 21:51 **/@Servicepublic class StepService extends BaseService {    @Autowired    private StepRepository stepRepository;    public void save(Step entity){        stepRepository.save(entity);    }    public void delete(String entityId){        stepRepository.delete(entityId);    }    public Page<Step> findAll(PageRequest pageRequest) {        return stepRepository.findAll(pageRequest);    }    public List<Step> findByTaskId(Long taskId) {        return stepRepository.findByTaskId(taskId);    }}