package com.testwa.distest.server.service.device.service;import com.testwa.distest.server.entity.DeviceLog;import com.testwa.distest.server.entity.User;import com.testwa.distest.server.service.device.dao.IDeviceLogDAO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;/** * @Program: distest * @Description: 设备使用日志 Service * @Author: wen * @Create: 2018-07-11 11:17 **/@Slf4j@Servicepublic class DeviceLogService {    @Autowired    private IDeviceLogDAO deviceLogDAO;    public void insert(DeviceLog entity) {        deviceLogDAO.insert(entity);    }    public Long sumDebugTime(List<User> members, Long startTime, Long endTime) {        return deviceLogDAO.sumDebugTime(members, startTime, endTime);    }    public Long sumDebugTime(User user) {        Long debugTime = deviceLogDAO.sumDebugTimeByUserCode(user.getUserCode(), null, null);        return debugTime == null?0:debugTime;    }    public Long sumJobTime(User user) {        Long jobTime = deviceLogDAO.sumJobTimeByUserCode(user.getUserCode(), null, null);        return jobTime == null?0:jobTime;    }}