package com.testwa.distest.server.service.device.service;import com.testwa.core.base.service.BaseService;import com.testwa.distest.server.entity.Api;import com.testwa.distest.server.entity.DeviceLog;import com.testwa.distest.server.entity.User;import com.testwa.distest.server.mapper.DeviceLogMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @Program: distest * @Description: 设备使用日志 Service * @Author: wen * @Create: 2018-07-11 11:17 **/@Slf4j@Servicepublic class DeviceLogService extends BaseService<DeviceLog, Long> {    @Autowired    private DeviceLogMapper deviceLogMapper;    public Long sumDebugTime(List<User> members, Long startTime, Long endTime) {        return deviceLogMapper.sumDebugTime(members, null, startTime, endTime);    }    public Long sumDebugTime(User user) {        Long debugTime = deviceLogMapper.sumDebugTimeByUserCode(user.getUserCode(), null, null);        return debugTime == null?0:debugTime;    }    public Long sumJobTime(User user) {        Long jobTime = deviceLogMapper.sumJobTimeByUserCode(user.getUserCode(), null, null);        return jobTime == null?0:jobTime;    }}