package com.testwa.distest.server.service.task.service;import com.testwa.core.base.service.BaseService;import com.testwa.distest.server.entity.AppiumFile;import com.testwa.distest.server.entity.ProjectMember;import com.testwa.distest.server.mapper.AppiumFileMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Slf4j@Service@Transactional(propagation = Propagation.REQUIRED, readOnly = true)public class AppiumFileService extends BaseService<AppiumFile, Long> {    @Autowired    private AppiumFileMapper appiumFileMapper;    public List<AppiumFile> findAll(Long taskCode){        return appiumFileMapper.selectListByProperty(AppiumFile::getTaskCode, taskCode);    }    public AppiumFile findOne(Long taskCode, String deviceId){        return appiumFileMapper.findOne(taskCode, deviceId);    }    @Transactional(propagation = Propagation.REQUIRED)    public void deleteByTask(Long taskCode){        appiumFileMapper.removeFromTask(taskCode);    }}