package com.testwa.distest.server.service.task.service;import com.testwa.distest.server.entity.AppiumFile;import com.testwa.distest.server.service.task.dao.IAppiumFileDAO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Slf4j@Service@Transactional(propagation = Propagation.REQUIRED, readOnly = true)public class AppiumFileService {    @Autowired    private IAppiumFileDAO appiumFileDAO;    public List<AppiumFile> findAll(Long taskCode){        return appiumFileDAO.findAll(taskCode);    }    public AppiumFile findOne(Long taskCode, String deviceId){        return appiumFileDAO.findOne(taskCode, deviceId);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void deleteByTask(Long taskCode){        appiumFileDAO.removeFromTask(taskCode);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public long save(AppiumFile entity) {        return appiumFileDAO.insert(entity);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void update(AppiumFile entity) {        appiumFileDAO.update(entity);    }}