package com.testwa.distest.server.service.task.service;import com.testwa.distest.server.entity.AppiumFile;import com.testwa.distest.server.service.task.dao.IAppiumFileDAO;import lombok.extern.log4j.Log4j2;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Log4j2@Service@Transactional(propagation = Propagation.REQUIRED, readOnly = true)public class AppiumFileService {    @Autowired    private IAppiumFileDAO appiumFileDAO;    public List<AppiumFile> findAll(Long taskId){        return appiumFileDAO.findAll(taskId);    }    public AppiumFile findOne(Long taskId, String deviceId){        return appiumFileDAO.findOne(taskId, deviceId);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void deleteByTaskId(Long taskId){        appiumFileDAO.removeFromTask(taskId);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public long save(AppiumFile entity) {        return appiumFileDAO.insert(entity);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void update(AppiumFile entity) {        appiumFileDAO.update(entity);    }}