package com.testwa.distest.server.service.task.service;import com.testwa.core.base.service.BaseService;import com.testwa.distest.server.entity.LogFile;import com.testwa.distest.server.entity.ProjectMember;import com.testwa.distest.server.mapper.LogFileMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Slf4j@Service@Transactional(propagation = Propagation.REQUIRED, readOnly = true)public class LogFileService extends BaseService<LogFile, Long> {    @Autowired    private LogFileMapper logFileMapper;    public List<LogFile> findAll(Long taskCode){        return logFileMapper.selectListByProperty(LogFile::getTaskCode, taskCode);    }    public LogFile findOne(Long taskId, String deviceId){        return logFileMapper.findOne(taskId, deviceId);    }    @Transactional(propagation = Propagation.REQUIRED)    public void deleteByTaskId(Long taskId){        logFileMapper.removeFromTask(taskId);    }}