package com.testwa.distest.server.service.task.service;import com.testwa.distest.server.entity.AppiumFile;import com.testwa.distest.server.entity.LoggerFile;import com.testwa.distest.server.service.task.dao.ILoggerFileDAO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Slf4j@Service@Transactional(propagation = Propagation.REQUIRED, readOnly = true)public class LoggerFileService {    @Autowired    private ILoggerFileDAO loggerFileDAO;    public List<LoggerFile> findAll(Long taskId){        return loggerFileDAO.findAll(taskId);    }    public LoggerFile findOne(Long taskId, String deviceId){        return loggerFileDAO.findOne(taskId, deviceId);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void deleteByTaskId(Long taskId){        loggerFileDAO.removeFromTask(taskId);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public long save(LoggerFile entity) {        return loggerFileDAO.insert(entity);    }    @Transactional(propagation = Propagation.REQUIRED, readOnly = false)    public void update(LoggerFile entity) {        loggerFileDAO.update(entity);    }}