package com.testwa.distest.server.startup;import com.testwa.distest.server.service.cache.mgr.ClientSessionMgr;import com.testwa.distest.server.service.cache.mgr.DeviceSessionMgr;import com.testwa.distest.server.service.cache.mgr.SubscribeMgr;import com.testwa.distest.server.web.device.auth.DeviceAuthMgr;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class CleanOldCache implements CommandLineRunner {    @Autowired    private SubscribeMgr subscribeMgr;    @Autowired    private DeviceSessionMgr deviceSessionMgr;    @Autowired    private ClientSessionMgr clientSessionMgr;    @Autowired    private DeviceAuthMgr deviceAuthMgr;    @Override    public void run(String... strings) throws Exception {        subscribeMgr.delAllSubscribes();        deviceSessionMgr.delAllDeviceSessions();        clientSessionMgr.delAllClientSessions();        deviceAuthMgr.delAllOnline();        deviceAuthMgr.delAllUsing();    }}