package com.testwa.distest.server.startup;import com.testwa.distest.server.service.cache.mgr.SubscribeMgr;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;@Slf4j@Componentpublic class CleanOldCache implements CommandLineRunner {    @Autowired    private SubscribeMgr subscribeMgr;    @Override    public void run(String... strings) throws Exception {        subscribeMgr.delAllSubscribes();    }}