package com.testwa.distest.server.web.task.vo;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.fasterxml.jackson.databind.ser.std.ToStringSerializer;import com.testwa.distest.server.entity.Device;import lombok.AllArgsConstructor;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * @Program: distest * @Description: * @Author: wen * @Create: 2018-05-29 21:07 **/@Datapublic class TaskStartResultVO {    @JsonSerialize(using=ToStringSerializer.class)    private Long taskCode;    private List<Device> runningDevices = new ArrayList<>();    private List<Device> unableDevices = new ArrayList<>();    public void addRunningDevice(Device device) {        this.runningDevices.add(device);    }    public void addUnableDevice(Device device) {        this.unableDevices.add(device);    }    public void addUnableDevice(List<Device> devices) {        this.unableDevices.addAll(devices);    }}