package com.testwa.distest.server.web.task.vo.echart;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * @Program: distest * @Description: 上下双曲线的数据格式 * @Author: wen * @Create: 2018-05-24 15:26 **/@Datapublic class EchartDoubleLine {    private List<String> x = new ArrayList<>();    private List<Object> up = new ArrayList<>();    private List<Object> down = new ArrayList<>();    public void add(String x, Object up, Object down) {        this.x.add(x);        this.up.add(up);        this.down.add(down);    }}